import{T as e}from"./index-1c9d67bf.js";import{_ as t,o as s,O as p,P as o,a as i}from"./@vue-3f1443a7.js";import"./zzjg-d629ae04.js";import"./index-f23c5fdf.js";import"./dayjs-4609adfa.js";import"./vue-router-932c2374.js";import"./pinia-b5a043bf.js";import"./element-plus-2b388df7.js";import"./lodash-es-3125e237.js";import"./@vueuse-c95eeb46.js";import"./@element-plus-c80408af.js";import"./@popperjs-b78c3215.js";import"./@ctrl-91de2ec7.js";import"./async-validator-cf877c1f.js";import"./memoize-one-63ab667a.js";import"./escape-html-c01bf6ee.js";import"./normalize-wheel-es-3222b0a2.js";import"./@floating-ui-e2b20dae.js";const a=i("span",null,"中心概述",-1),r={__name:"index",setup(i){const r=t([{title:"中心简介",name:"1",content:"<p>2022年9月，西南交通大学“可持续城市交通智能化教育部工程研究中心”(简称工程研究中心)获教育部批准立项建设,工程研究中心由计算机与人工智能学院牵头，与交通运输与物流学院、京东城市数字科技有限公司联合共建。工程研究中心将面向国家重大战略需求，围绕城市交通在运输调度、物流供应链、交通安全、出行减排等方面的理论与技术难题，聚焦可持续城市交通智能化开展创新研究与应用落地。</p>\n              <p>2023年2月28日，教育部科学技术与信息化司在成都组织召开了“可持续城市交通智能化教育部工程研究中心”建设规划可行性论证会，会议通过线上线下相结合的形式召开。论证专家组由来自战略支援部队第五十七研究所、电子科技大学、北京交通大学、东南大学、西南财经大学、四川省计算机研究院、中科院成都计算机应用研究所、四川省交通运输厅、河南省交通运输厅、成都市公安局、蜀道集团、成都市公交集团等高校、科研院所和交通相关领域专家组成，组长由中国科学院朱中梁院士担任。会议由西南交通大学科研院李怀龙副院长主持，何川副校长出席会议并讲话，计算机与人工智能学院、交通运输与物流学院、京东集团以及工程研究中心负责人以及技术骨干共30余人参加了此次论证会。</p>\n              <p>中心主任林良才教授详细汇报了中心的建设意义与必要性、主要研究方向和任务、验收目标和中长期目标、单位概况和保障条件、管理运行机制等内容。中心针对城市交通如何保障“人—车—路—环境”更安全、智能、低碳可持续发展需求，以“可持续城市交通智能化”为主线和抓手，聚焦“城市交通运输智能调度”、“城市物流供应链智能优化”、“城市交通安全智能运维”，以及“城市交通智慧出行减排”四个研究方向开展研究，形成示范性、引领性的可持续城市交通智能化应用创新成果。</p>"},{title:"未来展望",name:"2",content:"<p>工程研究中心的建设将聚焦城市交通的智能化和可持续性，助力国家交通强国、新一代人工智能技术及交通新基建的深度融合发展，符合学校未来重点发展定位</p>"}]);return(t,i)=>(s(),p(e,{tabValue:r,src:"pic0.png",editableTabsValue:"1"},{left:o((()=>[a])),_:1},8,["tabValue"]))}};export{r as default};
